mapper.insert:
insert into order_log (id, area_id,area_name, cabinet_no,
  cabinet_name, product_id, product_name, 
  product_num,submit_order_type, product_price, product_total_price,
  payment_time, payment_type, create_time
  )
values (#{id,jdbcType=INTEGER}, #{areaId,jdbcType=VARCHAR},#{areaName,jdbcType=VARCHAR}, #{cabinetNo,jdbcType=VARCHAR},
  #{cabinetName,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}, #{productName,jdbcType=VARCHAR}, 
  #{productNum,jdbcType=INTEGER},#{submitOrderType,jdbcType=INTEGER}, #{productPrice,jdbcType=BIGINT}, #{productTotalPrice,jdbcType=BIGINT},
  #{paymentTime,jdbcType=TIMESTAMP}, #{paymentType,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT areaName, cabinetNo, GROUP_CONCAT(productName,":",productTotalPrice) as totalPrice, GROUP_CONCAT(productName,":",productNum) as productNum, sum(productTotalPrice) as productTotalPrice, sum(productNum) as totalProductNum FROM ( SELECT product_name AS productName, sum(product_total_price) / 100 AS productTotalPrice, sum(product_num) AS productNum, area_name as areaName, cabinet_no AS cabinetNo
FROM order_log WHERE 1 = 1
and area_id=#{areaId}
and cabinet_no=#{cabinetNo}
and submit_order_type=#{submitOrderType}
and create_time > date_add(now(),interval -7 day)
and create_time BETWEEN #{startTime} AND #{endTime}
GROUP BY product_name, area_id, cabinet_no ) AS t1 group by areaName,cabinetNo;

select sum(product_total_price)/100 as totalProductPrices,sum(product_num) as totalProductNums from order_log
where 1=1


        and cabinet_no=#{cabinetNo}


        and area_id=#{areaId}





    and submit_order_type=#{submitOrderType}
