mapper.select:
SELECT

FROM cabinet a

WHERE a.id = #{id}

        SELECT

        FROM cabinet a




                AND a.cabinet_name = #{cabinetName}


                AND a.province = #{province}


                AND a.city = #{city}


                AND a.area = #{area}


                AND a.cabinet_status = #{cabinetStatus}


                AND a.cabinet_nos = #{cabinetNos}




                ORDER BY ${page.orderBy}


				order by create_time desc

SELECT

FROM cabinet a






        ORDER BY ${page.orderBy}

mapper.update:
UPDATE cabinet SET

    sys_password=#{sysPassword}



    food_password=#{foodPassword}



    sys_password=#{sysPassword},
    food_password=#{foodPassword}



WHERE id = #{cabinetId}

mapper.select:
select cabinet.cabinet_nos as cabinetNos,count(drawer.drawer_no) as total from drawer drawer join cabinet cabinet on cabinet.cabinet_nos=drawer.cabinet_no
where drawer.food_status=#{foodStatus} and drawer.drawer_status=1
group by cabinet.cabinet_nos;

select
a.id AS "id", a.cabinet_name AS "cabinetName", a.province AS "province", a.city AS "city", a.area AS "area", a.address AS "address", a.cabinet_status AS "cabinetStatus", a.create_time AS "createTime", a.cabinet_nos AS "cabinetNos", a.cabinet_replace_num as "cabinetReplaceNum", a.cabinet_full_num as "cabinetFullNum", a.food_password as "foodPassword", a.sys_password as "sysPassword"
from cabinet a where a.food_password=#{foodPass} and a.cabinet_nos=#{cabinetNo}
