mapper.select:
SELECT 

FROM drawer a

WHERE a.id = #{id}

SELECT 

FROM drawer a




		AND a.cabinet_id = #{cabinet.id}


		AND a.cabinet_name LIKE 
			'%'||#{cabinetName}||'%'
			'%'+#{cabinetName}+'%'
			concat('%',#{cabinetName},'%')


		AND a.drawer_status = #{drawerStatus}


		AND a.food_status = #{foodStatus}




		ORDER BY ${page.orderBy}

SELECT 

FROM drawer a






		ORDER BY ${page.orderBy}

mapper.update:
DELETE FROM drawer


		WHERE id = #{id}


		WHERE cabinet_id = #{cabinetId}
