mapper.select:
SELECT 

FROM cabinet_product_relaction a

WHERE a.id = #{id}

SELECT
FROM cabinet_product_relaction a
AND a.cabinet_no = #{cabinetNo}
AND a.cabinet_name LIKE '%'||#{cabinetName}||'%' '%'+#{cabinetName}+'%' concat('%',#{cabinetName},'%')
AND a.product_name LIKE '%'||#{productName}||'%' '%'+#{productName}+'%' concat('%',#{productName},'%')
AND a.cabinet_product_status = #{cabinetProductStatus}
ORDER BY ${page.orderBy}

SELECT 

FROM cabinet_product_relaction a






		ORDER BY ${page.orderBy}
